mapper.select:
select id, password, name, type
  from w_umember
 where id = #{id} and password = #{password}

select id, name, password, type
  from w_cmember
 where id = #{id} and password = #{password}

select id, password, type
  from w_umember
 where id = #{id} and password = #{password}

select id, password, name, tel, email, to_char(reg_date, 'yyyy-mm-dd') as regDate, type
from w_umember
where id= #{id}
